import { CHANGE_THEME_EVENT } from './theming-event.js';
import { getAllCSSVariables } from './utils.js';
let theme;
function dispatchThemingEvent(detail) {
    window.dispatchEvent(new CustomEvent(CHANGE_THEME_EVENT, { detail }));
}
function isOfTypeTheme(theme) {
    return ['bootstrap', 'material', 'indigo', 'fluent'].includes(theme);
}
export const getTheme = () => {
    if (!theme) {
        const [_, t] = Object.entries(getAllCSSVariables()).find(([v]) => v === 'igTheme') || [];
        theme = t && isOfTypeTheme(t) ? t : 'bootstrap';
    }
    return theme;
};
export const setTheme = (value) => {
    theme = value;
};
export const configureTheme = (t) => {
    if (isOfTypeTheme(t)) {
        setTheme(t);
    }
    dispatchThemingEvent({ theme });
};
