const CssKeyToJsKey = (key) => key.replace('--', '').replace(/-./g, (x) => x.toUpperCase()[1]);
const getAllCSSVariableNames = (styleSheets = document.styleSheets) => {
    const cssVars = [];
    Array.from(styleSheets).forEach((styleSheet) => {
        try {
            Array.from(styleSheet.cssRules).forEach((rule) => {
                if (!rule || !rule['style']) {
                    return;
                }
                Array.from(rule['style']).forEach((style) => {
                    if (style.startsWith('--') && cssVars.indexOf(style) == -1) {
                        cssVars.push(style);
                    }
                });
            });
        }
        catch (e) { }
    });
    return cssVars;
};
const getElementCSSVariables = (allCSSVars, element = document.body, pseudo = '') => {
    const elStyles = window.getComputedStyle(element, pseudo);
    const cssVars = {};
    allCSSVars.forEach((key) => {
        const value = elStyles.getPropertyValue(key);
        if (value) {
            cssVars[CssKeyToJsKey(key)] = value.trim();
        }
    });
    return cssVars;
};
export const getAllCSSVariables = () => {
    const cssVars = getAllCSSVariableNames();
    return getElementCSSVariables(cssVars, document.documentElement);
};
