export default class FilterDataOperation {
    apply(data, controller) {
        const { searchTerm, filteringOptions: { filterKey, caseSensitive }, } = controller;
        if (!searchTerm)
            return data;
        const term = caseSensitive ? searchTerm : searchTerm.toLocaleLowerCase();
        return data.filter((item) => {
            const value = filterKey ? String(item[filterKey]) : String(item);
            return caseSensitive
                ? value.includes(term)
                : value.toLocaleLowerCase().includes(term);
        });
    }
}
